"use strict";var yargs=require("yargs/yargs");var version=require("../version");var command={command:"help",describe:"Show general help and list all available commands",builder:{},run:function run(options,done){var args=yargs().detectLocale(false).exitProcess(false).version(false).help(false);Object.keys(options.commands).forEach(function(command){args.command(options.commands[command])});args.usage("TronBox v".concat(version.bundle," - a development framework for Tron\n\nUsage: tronbox <command> [options]")).epilog("See more at https://tronbox.io/docs/").showHelp();done()}};module.exports=command;